import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FinancialTracker = (props) => {
	const username = props.username;
	const roundCount = props.roundCount;
	const [ready, setReady] = useState();

	const [salary, setSalary] = useState(5000);
	const [totalIncome, setTotalIncome] = useState(0);
	const [totalExpenses, setTotalExpenses] = useState(0);
	const [totalLeft, setTotalLeft] = useState(0);

	const [incomeSource, setIncomeSource] = useState();
	const [incomeAmount, setIncomeAmount] = useState(0);

	const currentIncome = [];
	const currentExpenses = [];

	// use effect, watch if round count changes
	useEffect(() => {
		console.log('hey its a new round');
	}, [roundCount]);

	const expenses = (
		<div>
			<h2>Enter all your expenses this month</h2>
			<div>
				<h2>expenses</h2>
				<input type="number" placeholder="Expense Here"></input>
			</div>
		</div>
	);

	const addIncome = () => {
		let newIncome = {
			source: incomeSource,
			amount: incomeAmount,
		};

		currentIncome.push({
			source: incomeSource,
			amount: incomeAmount,
		});
		console.log(currentIncome);

		document.getElementById('incomeSource').value = '';
		document.getElementById('incomeAmount').value = '';
	};

	const income = (
		<div>
			<h2>Enter all your income this month</h2>
			<span>
				<p>Salary</p>
				<p>{salary}</p>
			</span>
			<span>
				<p>
					additional expenses. type the source and amount and click the button
					to add it.
				</p>
				<span>
					<input
						id="incomeSource"
						type="text"
						placeholder="Income Source"
						onChange={(e) => setIncomeSource(e.target.value)}></input>
					<input
						id="incomeAmount"
						type="number"
						placeholder="Income Amount"
						onChange={(e) => setIncomeAmount(e.target.value)}></input>
					<button onClick={addIncome}>Add income</button>
				</span>
			</span>

			<div>
				All income:
				{currentIncome.map((data) => (
					<div>
						<h3> {data.source}</h3>
						<p>{data.amount}</p>
					</div>
				))}
			</div>
		</div>
	);

	return (
		<div>
			<h1>new payday!</h1>
			<h2> {currentIncome}</h2>
			{income}
		</div>
	);
};

export default FinancialTracker;
