import queryString from 'query-string';

import axios from 'axios';

const Player = ({ location }) => {
	const [servername, setServername] = useState('');
	const [roundCounter, setRoundCounter] = useState();

	useEffect(() => {
		const { servername } = queryString.parse(location.search);

		setServername(servername);
	}, [location.search]);

	const updateRoundCounter = () => {
		axios
			.get('http://localhost:5000/serverRoom/currentRound/' + servername)
			.then((res) => {
				console.log(res.data);
				setRoundCounter(res.data.roundcounter);
