import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container } from '../shared/global';

import queryString from 'query-string';

import axios from 'axios';

const Player = ({ props, location }) => {
	// const socket = props.socket;
	const [username, setUsername] = useState('');
	const [servername, setServername] = useState('');
	const [roundCounter, setRoundCounter] = useState();

	useEffect(() => {
		const { username, servername } = queryString.parse(location.search);

		setUsername(username);
		setServername(servername);
	}, []);

	const updateRoundCounter = () => {
		let round = axios
			.get('http://localhost:5000/serverRoom/currentRound/' + servername)
			.then((res) => {
				console.log(res.data);
			});

		console.log(round);

		// setRoundCount();
	};

	return (
		<Container>
			<div>{servername}</div>
			<div>{username}</div>
			<div>{roundCounter}</div>
			<button onClick={updateRoundCounter}></button>
		</Container>
	);
};

export default Player;
