import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container } from '../shared/global';

import queryString from 'query-string';

import axios from 'axios';

const Player = ({ props, location }) => {
	// const socket = props.socket;
	const [username, setUsername] = useState('');
	const [servername, setServername] = useState('');
	const [roundCounter, setRoundCounter] = useState(0);

	const MINUTE_MS = 1000;

	useEffect(() => {
		const { username, servername } = queryString.parse(location.search);

		setUsername(username);
		setServername(servername);
	}, [location.search]);

	useEffect(() => {
		setTimeout(() => {
			const interval = setInterval(() => {
				updateRoundCounter();
			}, MINUTE_MS);

			return () => clearInterval(interval);
		}, 1000);
	}, [location.search]);

	const updateRoundCounter = () => {
		axios
			.get('http://localhost:5000/serverRoom/currentRound/' + servername)
			.then((res) => {
				setRoundCounter(res.data.roundcounter);
			});
		console.log(servername);
	};

	return (
		<Container>
			<div>{servername}</div>
			<div>{username}</div>
			<div>{roundCounter}</div>
		</Container>
	);
};

export default Player;
