import React, { useState } from 'react';
import { Container } from '../shared/global';
import axios from 'axios';
import careerData from './../careerData';

const ChooseCareer = () => {
	const [chosenCareer, setChosenCareer] = useState();
	const [chosenSalary, setChosenSalary] = useState();

	let salary;

	const selectOptions = (value) => {
		setChosenCareer(value);
		careerData
			.filter(({ careerName }) => careerName === chosenCareer)
			.map((data, index) => {
				setChosenSalary(data.startingSalary);
			});

		salary = <div>{chosenSalary}</div>;
		console.log(chosenSalary);
	};

	const pickCareer = (e) => {
		e.preventDefault();
	};

	return (
		<Container>
			<select
				onChange={(e) => {
					selectOptions(e.target.value);
				}}>
				<option disabled>Select One</option>
				{careerData.map((data, index) => (
					<option key={index}>{data.careerName}</option>
				))}
			</select>
			{salary}

			<button></button>
		</Container>
	);
};

export default ChooseCareer;
