import React from 'react';
import styled from 'styled-components';
import worldEventsData from '../worldEventsData';

const Container = styled.div`
	width: 100%;
	overflow: auto;
`;

const CurrentNewsContainer = styled.div`
	font-size: 4rem;
	transition: 2s;
`;

const EventCard = styled.div`
	width: 100%;
	border: #ccc 1px solid;
	padding: 0.5rem;
	margin: 0.5rem 0;
	border-radius: 5px;
`;

const PastEventsContainer = styled.div`
	display: flex;
	flex-direction: column-reverse;
`;

const WorldEvents = (props) => {
	// TODO: randomize order of world events

	const randomizeEvents = (roundCount) => {
		for (let i = 0; i < roundCount; i++) {
			let randomIndex = Math.floor(Math.random(0, roundCount));
			console.log(randomIndex);

			let events = worldEventsData.map((data, index) => (
				<CurrentNewsContainer key={index}>{data.text}</CurrentNewsContainer>
			));
		}
	};

	// props.roundCounter > 1
	// 	? worldEventsData
	// 			.slice(2, props.roundCounter)
	// 			.map((data, index) => <EventCard key={index}>{data.text}</EventCard>)
	// 	: null;
	return (
		<Container>
			<h1>World Events</h1>
			<h2>Latest</h2>
			{/* TODO: add a placeholder for no news yet */}
			{/* {props.roundCounter > 1
				? worldEventsData.map((data, index) =>
						index === props.roundCounter ? (
							<CurrentNewsContainer key={index}>
								{data.text}
							</CurrentNewsContainer>
						) : null
				  )
				: null} */}
			{randomizeEvents(props.roundCounter)}
			<h2>Past</h2>
			<PastEventsContainer></PastEventsContainer>
		</Container>
	);
};

export default WorldEvents;
