import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import glossary from './../glossary';

const StyledSearchBar = styled.input`
	font-size: 1rem;
	padding: 1rem;
	border: #ccc solid 1px;
	border-radius: 5px;
	width: 50%;
`;

const GlossarySearch = (props) => {
	const [filterTerm, setFilterTerm] = useState(initialState);
	// const filterGlossary = glossaryItem.filter((item) => {
	// 	return Object.keys(item).some((key) =>
	// 		item[key].toLowerCase().includes(lowercasedFilter)
	// 	);
	// });

	const glossaryItem = glossary
		.filter(({ term }) => term === filterTerm)
		.map((data) => (
			<div key={data.term}>
				<h1>{data.term}</h1>
				<p>{data.definition}</p>
			</div>
		));

	return (
		<div>
			<StyledSearchBar
				value={filterTerm}
				placeholder="Search for Terms"></StyledSearchBar>
			{glossaryItem}
		</div>
	);
};

export default GlossarySearch;
