import React from 'react';
import styled from 'styled-components';

const StyledSearchBar = styled.input`
	font-size: 1rem;
	padding: 1rem;
	border: #ccc solid 1px;
	border-radius: 5px;
	width: 50%;
`;

const GlossarySearch = ({ keyword, setKeyword }) => {
	return (
		<StyledSearchBar
			value={keyword}
			placeholder="Search for Terms"
			onSubmit={(e) => setKeyword(e.target.value)}></StyledSearchBar>
	);
};

export default GlossarySearch;
