import React, { useState } from 'react';
import { Container, H1, PrimaryButton } from '../shared/global';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateServer from './CreateServer';

const Game = () => {
	const [username, setUsername] = useState();
	const [role, setRole] = useState();
	const [serverName, setServerName] = useState();
	const [ready, setReady] = useState(false);

	const beginGame = (e) => {
		e.preventDefault();
		console.log(username + ' is a ' + role);

		const user = {
			username: username,
			role: role,
		};

		axios
			.post('http://localhost:5000/users/add', user)
			.then((res) => console.log(res.data));

		setReady(true);
	};

	const createServer = (e) => {
		e.preventDefault();
		console.log('server name is' + serverName);

		const serverInfo = {
			servername: serverName,
		};

		axios
			.post('http://localhost:5000/serverRoom/add', serverInfo)
			.then((res) => console.log(res.data));
	};

	return (
		<Container>
			{ready === false ? (
				<div>
					<H1>What is your role?</H1>

					<label for="username">Username</label>
					<input
						placeholder="Write your username here"
						onChange={(e) => {
							setUsername(e.target.value);
						}}></input>

					<div
						onChange={(e) => {
							setRole(e.target.value);
						}}>
						<input type="radio" id="player" name="role" value="player"></input>
						<label for="player">Player</label>
						<br></br>

						<input
							type="radio"
							id="gamemaster"
							name="role"
							value="gamemaster"></input>
						<label for="gamemaster">Gamemaster</label>
						<br></br>
					</div>
					<PrimaryButton onClick={beginGame}>Submit</PrimaryButton>
				</div>
			) : (
				<div>
					{role === 'player' ? (
						<span>
							<H1>Join Server</H1>
							<label for="username">Server</label>
							<input
								placeholder="Find a Server"
								onChange={(e) => {
									setServerName(e.target.value);
								}}></input>
							<Link to={`/player?servername=${serverName}`}>
								<button>Join</button>
							</Link>
						</span>
					) : (
						<span>
							<H1>Host Server</H1>
							<label for="username">Server</label>
							<input
								placeholder="Create a Server"
								onChange={(e) => {
									setServerName(e.target.value);
								}}></input>
							<Link
								onClick={(e) =>
									!serverName ? e.preventDefault() : CreateServer()
								}
								to={`/gamemaster?servername=${serverName}`}>
								<button>Create </button>
							</Link>
						</span>
					)}
				</div>
			)}
		</Container>
	);
};

export default Game;
