	// 	return (
	// 		<StockCard
	// 			stockTicker={stock.ticker}
	// 			stockName={stock.stockName}
	// 			currentPrice={stock.startingPrice}
	// 			priceRange={stock.priceRange}
	// 			key={stock.ticker}></StockCard>
	// 	);
	// });

	const stocks = stockMarketData
		.filter(({ term }) => term === filterTerm.toLowerCase())
		.map((data) => (
			<GlossaryItemContainer key={data.term}>
				<GlossaryTerm>{data.term}</GlossaryTerm>
				<p>{data.definition}</p>
			</GlossaryItemContainer>
		));
	return (
		<Container>
			<h1>Stock Market</h1>
			{stocks}
		</Container>
	);
};

export default StockMarket;
