import React, { useCallback, useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { Container, PrimaryButton } from './../shared/global';
import RoundTracker from './../components/RoundTracker';
import WorldEvents from '../components/WorldEvents';
import StockMarket from '../components/StockMarket';

const Grid = styled.div`
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	grid-column-gap: 1rem;
	padding: 0 1rem;
`;

const DashboardItem = styled.div`
	grid-column: span 1;
	padding: 1rem;
	border: #ccc 1px solid;
	border-radius: 5px;
	margin: 1rem 0;
	overflow: auto;
	max-height: calc(100vh - 75px - 50px - 2rem);
`;

const RoundCounter = styled.h1`
	font-size: 5rem;
	margin: 2rem 0;
`;

const Modal = styled.div`
	background-color: #fff;
	position: absolute;
	left: 0;
	right: 0;
	border: #ccc solid 1px;
	margin: 1rem auto;
	width: 100%;
	max-width: 500px;
	padding: 1rem;
	border-radius: 5px;
	color: #000;
	text-align: center;
`;

const ButtonContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
`;

const Game = () => {
	const [roundCount, setRoundCount] = useState(0);
	const [openDialog, setOpenDialog] = useState(false);

	const history = useHistory();
	return (
		<Container
			style={{
				padding: '0',
			}}>
			<Grid>
				<DashboardItem style={{ padding: '0rem' }}>
					<RoundCounter> {roundCount}</RoundCounter>

					<PrimaryButton onClick={() => setRoundCount(roundCount + 1)}>
						Next Round
					</PrimaryButton>
					<PrimaryButton onClick={() => setOpenDialog(true)}>
						End Game
					</PrimaryButton>

					{openDialog === true ? (
						<Modal>
							<h2>Are you sure you want to end the game?</h2>
							<ButtonContainer>
								<PrimaryButton onClick={() => setOpenDialog(false)}>
									No, continue game
								</PrimaryButton>
								<PrimaryButton onClick={() => history.push('/play-again')}>
									Yes, end game
								</PrimaryButton>
							</ButtonContainer>
						</Modal>
					) : null}
				</DashboardItem>
				<DashboardItem>
					<WorldEvents></WorldEvents>
				</DashboardItem>
				<DashboardItem>
					<StockMarket></StockMarket>
				</DashboardItem>
			</Grid>
		</Container>
	);
};

export default Game;
