import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { CardHeading, Container, H1, H2, HeadingImg } from '../shared/global';
import ChooseCareer from './../components/ChooseCareer';

import queryString from 'query-string';

import axios from 'axios';
import SetBudget from '../components/SetBudget';
import SetFinancialGoal from '../components/SetFinancialGoal';
import FinancialTracker from '../components/FinancialTracker';

import Career from './../assets/Icon/Career.png';
import Bank from './../assets/Icon/Bank.png';
import Goal from './../assets/Icon/Goal.png';
import Budget from './../assets/Icon/Budget.png';
import Stocks from './../assets/Icon/Stocks.png';
import Calendar from './../assets/Icon/Calendar.png';

const Grid = styled.div`
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	grid-column-gap: 1rem;
	padding: 0 1rem;
`;

const DashboardItem = styled.div`
	grid-column: span 1;
	/* padding: 1rem;
	background-color: white; */
	margin: 1rem 0;
	overflow: auto;
	/* height: calc(100vh - 75px - 50px - 2rem); */
	border-radius: 5px;
`;

const GameStrip = styled.div`
	width: 100%;
	color: #fff;
	background-color: #4f73b6;
	display: flex;
	justify-content: space-between;
	padding: 0.5rem 1rem;
	font-size: 1.25rem;
`;

const CareerCard = styled.div`
	border-radius: 5px;
	background-color: #4f73b6;
	color: #fff;
	padding: 1rem;
	margin-bottom: 1rem;
`;

const DashboardCard = styled.div`
	border-radius: 5px;
	background-color: #fff;
	color: #111;
	padding: 1rem;
	margin-bottom: 1rem;
`;

const Player = ({ location }) => {
	const [username, setUsername] = useState('');
	const [servername, setServername] = useState('');
	const [roundCounter, setRoundCounter] = useState(0);
	const [career, setCareer] = useState(false);
	const [budget, setBudget] = useState(false);
	const [goal, setGoal] = useState(false);

	let currentServerName = '';

	const MINUTE_MS = 500;

	useEffect(() => {
		const { username, servername } = queryString.parse(location.search);

		setUsername(username);
		setServername(servername);
		currentServerName = servername;
	}, [location.search]);

	useEffect(() => {
		const interval = setInterval(() => {
			axios
				.get(
					'http://localhost:5000/serverRoom/currentRound/' + currentServerName
				)
				.then((res) => {
					setRoundCounter(res.data.roundcounter);
				});

			console.log(currentServerName);
		}, MINUTE_MS);

		return () => clearInterval(interval);
	}, []);

	console.log(career, budget, goal);

	return (
		<Container
			style={{
				padding: '0',
			}}>
			<GameStrip>
				<span>Server: {servername}</span>
				<span style={{ fontWeight: '700' }}>{username}'s Dashboard</span>
				<span>Round {roundCounter}</span>
			</GameStrip>

			{career === true ? null : (
				<div>
					<ChooseCareer username={username}></ChooseCareer>
					<button onClick={(e) => setCareer(true)}></button>
				</div>
			)}

			{budget === false ? (
				<div>
					<SetBudget username={username}></SetBudget>
					<button onClick={(e) => setBudget(true)}></button>
				</div>
			) : null}

			{goal === false ? (
				<div>
					<SetFinancialGoal username={username}></SetFinancialGoal>{' '}
					<button onClick={(e) => setGoal(true)}></button>
				</div>
			) : null}

			{/* <Grid>
				<DashboardItem>
					<CareerCard>
						<CardHeading>
							<HeadingImg src={Career}></HeadingImg>
							<H2>My Career</H2>
						</CardHeading>
					</CareerCard>
					<DashboardCard>
						<CardHeading>
							<HeadingImg src={Bank}></HeadingImg>
							<H2>My Bank Accounts</H2>
						</CardHeading>
					</DashboardCard>
					<DashboardCard>
						<CardHeading>
							<HeadingImg src={Goal}></HeadingImg>
							<H2>My Goal</H2>
						</CardHeading>
					</DashboardCard>
				</DashboardItem>
				<DashboardItem>
					<DashboardCard>
						<CardHeading>
							<HeadingImg src={Budget}></HeadingImg>
							<H2>My Budget</H2>
						</CardHeading>
					</DashboardCard>
					<DashboardCard>
						<CardHeading>
							<HeadingImg src={Stocks}></HeadingImg>
							<H2>My Stocks</H2>
						</CardHeading>
					</DashboardCard>
				</DashboardItem>
				<DashboardItem>
					<DashboardCard>
						<CardHeading>
							<HeadingImg src={Calendar}></HeadingImg>
							<H2>Monthly Finance Tracker</H2>
						</CardHeading>
						<FinancialTracker
				username={username}
				roundCount={roundCounter}></FinancialTracker>
					</DashboardCard>
				</DashboardItem>
			</Grid> */}
		</Container>
	);
};

export default Player;
