import React from 'react';
import styled from 'styled-components';
import { Link, useLocation } from 'react-router-dom';
import { FlexBetween, ClearButton } from './global';
import GlossarySearch from './../components/GlossarySearch';

const Nav = styled.nav`
	width: 100%;
	padding: 1rem 2rem;
	background-color: white;
	display: flex;
	justify-content: space-between;
	border-bottom: #ccc solid 1px;
`;

const NavMenu = styled.ul`
	display: flex;
	justify-content: space-between;
	width: 25%;
`;

const NavItem = styled.li`
	list-style-type: none;
`;

const StyledLink = styled(Link)`
	color: #000;
	text-decoration: none;
`;

const NavBar = () => {
	const location = useLocation();

	if (location.pathname !== '/game') {
		return (
			<Nav>
				<StyledLink to="/">
					<img alt="Logo"></img>
				</StyledLink>

				<NavMenu>
					<NavItem>
						<StyledLink to="/">Play</StyledLink>
					</NavItem>
					<NavItem>
						<StyledLink to="/guidebook">Guidebook</StyledLink>
					</NavItem>
					<NavItem>
						<StyledLink to="/glossary">Glossary</StyledLink>
					</NavItem>
					<NavItem>
						<StyledLink to="/about">About</StyledLink>
					</NavItem>
					<NavItem>
						<StyledLink to="/help">Help</StyledLink>
					</NavItem>
				</NavMenu>
			</Nav>
		);
	} else {
		return (
			<Nav>
				<FlexBetween style={{ alignItems: 'center', justifyContent: 'center' }}>
					<StyledLink to="/">
						<img alt="Logo"></img>
					</StyledLink>
					<GlossarySearch></GlossarySearch>
					<ClearButton>View Guidebook</ClearButton>
				</FlexBetween>
			</Nav>
		);
	}
};

export default NavBar;
