import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

import {
	FlexBetween,
	H3,
	H4,
	P,
	PrimaryButton,
	PrimaryButtonOutline,
	SmallOutlineButton,
	TextInput,
} from './../shared/global';

const InputRow = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	border: 1px #eee solid;
	border-radius: 5px;
	padding: 0.5rem 0.25rem;
	margin: 0.5rem 0;
`;

const CenteredRow = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
`;

const StyledTextInput = styled(TextInput)`
	font-size: 1rem;
	padding: 0.25rem;
	margin: 0 0.25rem;
`;

const FinancialTracker = (props) => {
	const username = props.username;
	const roundCount = props.roundCount;
	const [ready, setReady] = useState();
	const [incomeReady, setIncomeReady] = useState(false);
	const [expenseReady, setExpenseReady] = useState(false);
	const [transferReady, setTransferReady] = useState(false);

	const [salary, setSalary] = useState(5000);
	const [totalIncome, setTotalIncome] = useState(0);
	const [totalExpenses, setTotalExpenses] = useState(0);
	const [totalLeft, setTotalLeft] = useState(0);

	const [incomeSource, setIncomeSource] = useState();
	const [incomeAmount, setIncomeAmount] = useState(0);

	let total;

	const [incomeEntries, setIncomeEntries] = useState([{}]);
	const [expenseEntries, setExpenseEntries] = useState([{}]);

	const [inputValue, setInputValue] = useState('');

	const currentExpenses = [];
	// use effect, watch if round count changes
	useEffect(() => {
		console.log('hey its a new round');
	}, [roundCount]);

	useEffect(() => {
		setIncomeEntries([
			...incomeEntries,
			{
				source: 'Salary',
				amount: Number(salary),
			},
		]);

		setTotalIncome(salary);
	}, []);

	const expenses = (
		<div>
			<h2>Enter all your expenses this month</h2>
			<div>
				<h2>expenses</h2>
				<input type="number" placeholder="Expense Here"></input>
			</div>
		</div>
	);

	const addIncome = () => {
		if (incomeAmount != null && incomeSource != null) {
			if (incomeAmount != '' && incomeSource != 'null') {
				setIncomeEntries([
					...incomeEntries,
					{
						source: incomeSource,
						amount: Number(incomeAmount),
					},
				]);

				setTotalIncome(+totalIncome + +incomeAmount);
				document.getElementById('incomeSource').value = '';
				document.getElementById('incomeAmount').value = '';

				setIncomeSource('');
				setIncomeAmount('');
			} else {
				alert('enter an amount');
			}
		} else {
			alert('enter an amount');
		}
	};

	const income = (
		<div>
			<FlexBetween>
				<H3>Enter all your income this month</H3>
			</FlexBetween>

			<span>
				<InputRow>
					<StyledTextInput
						id="incomeSource"
						type="text"
						placeholder="Income Source"
						onChange={(e) => setIncomeSource(e.target.value)}></StyledTextInput>
					<StyledTextInput
						id="incomeAmount"
						type="number"
						placeholder="Income Amount"
						onChange={(e) => setIncomeAmount(e.target.value)}></StyledTextInput>
				</InputRow>
				<CenteredRow>
					<SmallOutlineButton onClick={addIncome}>
						Add income
					</SmallOutlineButton>
				</CenteredRow>
			</span>

			<div>
				<H3>Income Entries</H3>
				{incomeEntries.map((data, index) =>
					index > 0 ? (
						<InputRow key={index}>
							<H3> {data.source}</H3>
							<P>{data.amount}</P>
						</InputRow>
					) : null
				)}
				<H3 style={{ textAlign: 'right' }}>Total income: {totalIncome}</H3>
			</div>
		</div>
	);

	return (
		<div>
			{income}
			{incomeReady === true ? (
				income
			) : expenseReady === true ? (
				<div>expense</div>
			) : transferReady === true ? (
				<div>transfer</div>
			) : null}
			<PrimaryButton>Next</PrimaryButton>
		</div>
	);
};

export default FinancialTracker;
