import React from 'react';
import styled from 'styled-components';
import worldEventsData from '../worldEventsData';

const Container = styled.div`
	width: 100%;
	overflow: auto;
`;

const CurrentNewsContainer = styled.div`
	font-size: 4rem;
`;

const EventCard = styled.div`
	width: 100%;
	border: #ccc 1px solid;
	padding: 0.5rem;
	margin: 0.5rem 0;
	border-radius: 5px;
`;

const WorldEvents = (props) => {
	return (
		<Container>
			<h1>World Events</h1>
			<h2>Latest</h2>
			<CurrentNewsContainer>
				{worldEventsData[props.roundCounter].map((data) => (
					<EventCard>{data.text}</EventCard>
				))}
			</CurrentNewsContainer>
			<h2>Past</h2>
			<EventCard>My name is Roneilla</EventCard>
			{worldEventsData.slice(0, props.roundCounter).map((data) => (
				<EventCard>{data.text}</EventCard>
			))}
		</Container>
	);
};

export default WorldEvents;
