import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container } from '../shared/global';

import queryString from 'query-string';
import io from 'socket.io-client';

let connectionOptions = {
	'force new connection': true,
	reconnectionAttempts: 'Infinity',
	timeout: 1000,
	forceNew: true,
	transports: ['websocket'],
};

let socket;

const Player = ({ props, location }) => {
	// const socket = props.socket;
	const [username, setUsername] = useState('');
	const [servername, setServername] = useState('');
	const [message, setMessage] = useState('');
	const [messages, setMessages] = useState([]);

	const ENDPOINT = 'http://localhost:5000';

	useEffect(() => {
		const { username, servername } = queryString.parse(location.search);

		socket = io.connect(ENDPOINT, connectionOptions);

		setUsername(username);
		setServername(servername);

		// socket.emit('join', { username, servername }, ({}) => {});
	}, [ENDPOINT, location.search]);

	return (
		<Container socket={socket} servername={servername}>
			<div>{servername}</div>
			<div>{username}</div>
		</Container>
	);
};

export default Player;
