import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import {
	PrimaryButton,
	CardHeading,
	Container,
	H2,
	HeadingImg,
	H3,
	P,
} from '../shared/global';
import ChooseCareer from './../components/ChooseCareer';

import queryString from 'query-string';

import axios from 'axios';
import SetBudget from '../components/SetBudget';
import SetFinancialGoal from '../components/SetFinancialGoal';
import FinancialTracker from '../components/FinancialTracker';

import Career from './../assets/Icon/Career.png';
import Bank from './../assets/Icon/Bank.png';
import Goal from './../assets/Icon/Goal.png';
import Budget from './../assets/Icon/Budget.png';
import Stocks from './../assets/Icon/Stocks.png';
import Calendar from './../assets/Icon/Calendar.png';

const SetupCard = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	height: calc(100vh - 75px - 50px - 2rem);
`;

const Grid = styled.div`
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	grid-column-gap: 1rem;
	padding: 0 1rem;
	height: calc(100vh - 75px - 50px - 2rem);
`;

const DashboardItem = styled.div`
	grid-column: span 1;
	display: flex;
	flex-direction: column;
	margin: 1rem 0;
	overflow: auto;
	border-radius: 5px;
`;

const DashboardCard = styled.div`
	border-radius: 5px;
	background-color: #fff;
	color: #111;
	padding: 1rem;
	margin-bottom: 1rem;
	flex-grow: 1;
`;

const GameStrip = styled.div`
	width: 100%;
	color: #fff;
	background-color: #4f73b6;
	display: flex;
	justify-content: space-between;
	padding: 0.5rem 1rem;
	font-size: 1.25rem;
`;

const CareerCard = styled.div`
	border-radius: 5px;
	background-color: #4f73b6;
	color: #fff;
	padding: 1rem;
	margin-bottom: 1rem;
`;

const InputRow = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	border: 1px #eee solid;
	border-radius: 5px;
	padding: 0.5rem;
`;

const Player = ({ location }) => {
	const [username, setUsername] = useState('');
	const [servername, setServername] = useState('');
	const [roundCounter, setRoundCounter] = useState(0);
	const [career, setCareer] = useState(false);
	const [budget, setBudget] = useState(false);
	const [goal, setGoal] = useState(false);
	const [newMonth, setNewMonth] = useState(true);

	const [myCareer, setMyCareer] = useState();
	const [mySalary, setMySalary] = useState();
	const [myChequing, setMyChequing] = useState();
	const [mySavings, setMySavings] = useState();
	const [myTotal, setMyTotal] = useState();

	let currentServerName = '',
		currentUsername = '';

	const MINUTE_MS = 500;

	useEffect(() => {
		const { username, servername } = queryString.parse(location.search);

		setUsername(username);
		setServername(servername);
		currentUsername = username;
		currentServerName = servername;
	}, [location.search]);

	useEffect(() => {
		axios.get('http://localhost:5000/users/' + currentUsername).then((res) => {
			if (res.data.career != null) {
				setCareer(true);
			}

			if (res.data.budget != null) {
				setBudget(true);
			}

			if (res.data.financialGoal != null) {
				setGoal(true);
			}

			setMyCareer(res.data.career);
			setMySalary(res.data.salary);
		});
	}, []);

	useEffect(() => {
		const interval = setInterval(() => {
			axios
				.get(
					'http://localhost:5000/serverRoom/currentRound/' + currentServerName
				)
				.then((res) => {
					setRoundCounter(res.data.roundcounter);
				});

			console.log(currentServerName);
		}, MINUTE_MS);

		return () => clearInterval(interval);
	}, []);

	return (
		<Container
			style={{
				padding: '0',
			}}>
			<GameStrip>
				<span>Server: {servername}</span>
				<span style={{ fontWeight: '700' }}>{username}'s Dashboard</span>
				<span>Round {roundCounter}</span>
			</GameStrip>

			{career === false ? (
				<SetupCard>
					<CardHeading>
						<HeadingImg src={Career}></HeadingImg>
						<H2>Choose a Career</H2>
					</CardHeading>
					<ChooseCareer username={username}></ChooseCareer>
					<PrimaryButton onClick={(e) => setCareer(true)}>Next</PrimaryButton>
				</SetupCard>
			) : budget === false ? (
				<SetupCard>
					<CardHeading>
						<HeadingImg src={Budget}></HeadingImg>
						<H2>Set a Budget</H2>
					</CardHeading>
					<SetBudget username={username}></SetBudget>
					<PrimaryButton onClick={(e) => setBudget(true)}>Next</PrimaryButton>
				</SetupCard>
			) : goal === false ? (
				<SetupCard>
					<SetFinancialGoal username={username}></SetFinancialGoal>
					<PrimaryButton onClick={(e) => setGoal(true)}>Next</PrimaryButton>
				</SetupCard>
			) : (
				<Grid>
					<DashboardItem>
						<CareerCard>
							<CardHeading>
								<HeadingImg src={Career}></HeadingImg>
								<H2>My Career</H2>
							</CardHeading>
							<div>
								<H3>{myCareer}</H3>
								<P>{mySalary}</P>
							</div>
						</CareerCard>
						<DashboardCard>
							<CardHeading>
								<HeadingImg src={Bank}></HeadingImg>
								<H2>My Bank Accounts</H2>
							</CardHeading>
							<div>
								<InputRow></InputRow>
							</div>
						</DashboardCard>
						<DashboardCard>
							<CardHeading>
								<HeadingImg src={Goal}></HeadingImg>
								<H2>My Goal</H2>
							</CardHeading>
						</DashboardCard>
					</DashboardItem>
					<DashboardItem>
						<DashboardCard>
							<CardHeading>
								<HeadingImg src={Budget}></HeadingImg>
								<H2>My Budget</H2>
							</CardHeading>
						</DashboardCard>
						<DashboardCard>
							<CardHeading>
								<HeadingImg src={Stocks}></HeadingImg>
								<H2>My Stocks</H2>
							</CardHeading>
						</DashboardCard>
					</DashboardItem>
					<DashboardItem>
						<DashboardCard>
							<CardHeading>
								<HeadingImg src={Calendar}></HeadingImg>
								<H2>Monthly Finance Tracker</H2>
							</CardHeading>
							<FinancialTracker
								username={username}
								roundCount={roundCounter}></FinancialTracker>
						</DashboardCard>
					</DashboardItem>
				</Grid>
			)}
		</Container>
	);
};

export default Player;
