import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { P } from '../shared/global';
import stockMarketData from './../stockMarketData';

const Container = styled.div`
	width: 100%;
	overflow: auto;
`;

const StockCardContainer = styled.div`
	width: 100%;
	border: #ccc 1px solid;
	padding: 0.5rem;
	margin: 0.5rem 0;
	border-radius: 5px;
	display: flex;
	justify-content: space-between;
	align-items: center;
`;

const StockCard = (props, receiveAmount) => {
	const [stockPrice, setStockPrice] = useState(0);
	const prevAmount = useRef({ receiveAmount }).current;

	const MINUTE_MS = 1000; // 5 minutes

	useEffect(() => {
		const interval = setInterval(() => {
			setStockPrice(
				Math.floor(
					Math.random(props.startPrice, props.startPrice + props.priceRange) *
						1000
				)
			);
			console.log('Logs every minute');
		}, MINUTE_MS);

		return () => clearInterval(interval);
	}, []);

	useEffect(() => {
		const interval = setInterval(() => {
			if (prevAmount.receiveAmount < receiveAmount) {
				console.log(receiveAmount + prevAmount.receiveAmount);
			} else {
				console.log('lower');
			}
			return () => {
				prevAmount.receiveAmount = receiveAmount;
			};
		}, MINUTE_MS);

		return () => clearInterval(interval);
	}, [receiveAmount]);
	// const updateArrow = () => {
	// 	stockPrice > lastPrice ? <div>arrow up</div> : <div>arrow down</div>;
	// 	lastPrice = stockPrice;
	// };

	return (
		<StockCardContainer>
			<h3>{props.stockTicker}</h3>
			<h3>{props.stockName}</h3>
			<p>{stockPrice}</p>
			<span>
				{/* {(() => {
					currentPrice = stockPrice;
					let arrow;

					if (currentPrice > lastPrice) {
						arrow = <div>arrow up</div>;
					} else if (currentPrice < lastPrice) {
						arrow = <div>arrow down</div>;
					} else {
						arrow = <div>equal</div>;
					}

					return arrow;
					lastPrice = currentPrice;
					console.log(
						'current price is' + currentPrice + 'last price is' + lastPrice
					);
				})()} */}
			</span>
		</StockCardContainer>
	);
};

const StockMarket = () => {
	const stocks = stockMarketData.map((stock) => (
		<StockCard
			stockTicker={stock.ticker}
			stockName={stock.stockName}
			currentPrice={stock.startingPrice}
			priceRange={stock.priceRange}
			key={stock.ticker}></StockCard>
	));

	return (
		<Container>
			<h1>Stock Market</h1>
			{stocks}
		</Container>
	);
};

export default StockMarket;
