import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import stockMarketData from './../stockMarketData';

const Container = styled.div`
	width: 100%;
	overflow: auto;
`;

const StockCardContainer = styled.div`
	width: 100%;
	border: #ccc 1px solid;
	padding: 0.5rem;
	margin: 0.5rem 0;
	border-radius: 5px;
	display: flex;
	justify-content: space-between;
	align-items: center;
`;

const StockCard = (stockName, stockTicker, startingPrice, priceRange) => {
	// const [stockPrice, setstockPrice] = useState(0);
	let currentPrice, lastPrice;

	const MINUTE_MS = 300000; // 5 minutes

	// useEffect(() => {
	// 	const interval = setInterval(() => {
	// 		// currentPrice = Math.random(startPrice, priceRange);

	// 		console.log('Logs every minute');
	// 	}, MINUTE_MS);

	// 	return () => clearInterval(interval);
	// }, []);

	return (
		<StockCardContainer>
			<h3>{stockTicker}</h3>
			<h3>{stockName}</h3>
			<p>{startingPrice}</p>
			<p>{priceRange}</p>
			<span>
				{currentPrice > lastPrice ? <div>arrow up</div> : <div>arrow down</div>}
			</span>
		</StockCardContainer>
	);
};

const StockMarket = () => {
	const stocks = stockMarketData.map((stock) => {
		<StockCard
			stockTicker={stock.ticker}
			stockName={stock.stockName}
			currentPrice={stock.startingPrice}
			priceRange={stock.priceRange}
			key={stock.ticker}></StockCard>;
	});

	return (
		<Container>
			<h1>Stock Market</h1>
			<span>{stocks}</span>
			<span> stocks</span>
			<StockCardContainer>Hello</StockCardContainer>
		</Container>
	);
};

export default StockMarket;
