import React, { useCallback } from 'react';
import styled from 'styled-components';
import { Container } from './../shared/global';
import RoundTracker from './../components/RoundTracker';
import WorldEvents from '../components/WorldEvents';

const Grid = styled.div`
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	grid-column-gap: 1rem;
	height: 100%;
`;

const DashboardItem = styled.div`
	grid-column: span 1;
	padding: 1rem;
	border: #ccc 1px solid;
	border-radius: 5px;
	margin: 1rem 0;
	height: 100%;
	overflow-y: scroll;
`;

const Game = () => {
	return (
		<Container style={{ padding: '0 1rem 0 0', maxHeight: '80vh' }}>
			<Grid>
				<RoundTracker style={{ height: '100%' }}></RoundTracker>
				<DashboardItem style={{ height: '100%' }}>
					<WorldEvents style={{ height: '100%' }}></WorldEvents>
				</DashboardItem>
				<DashboardItem style={{ height: '100%' }}>3</DashboardItem>
			</Grid>
		</Container>
	);
};

export default Game;
