	width: 100%;
	overflow: auto;
`;

const StockCard = styled.div`
	width: 100%;
	border: #ccc 1px solid;
	padding: 0.5rem;
	margin: 0.5rem 0;
	border-radius: 5px;
	display: flex;
	justify-content: space-between;
	align-items: center;
`;

const StockCards = () => {
	const [stockPrice, setstockPrice] = useState(0);
	let currentPrice, lastPrice;

	const MINUTE_MS = 300000;

