import React, { useState } from 'react';
import styled from 'styled-components';
import glossary from './../glossary';

import { Container } from './../shared/global';

const StyledSearchBar = styled.input`
	font-size: 1rem;
	padding: 1rem;
	border: #ccc solid 1px;
	border-radius: 5px;
	position: relative;
	width: 100%;
	max-width: 800px;
	margin: 0 auto;
`;

const Wrapper = styled.div`
	text-align: center;
`;

const GlossaryPage = (props) => {
	const [filterTerm, setFilterTerm] = useState('');

	const allGlossaryItems = glossary.map((data) => (
		<div key={data.term}>
			<h1>{data.term}</h1>
			<p>{data.definition}</p>
		</div>
	));

	const glossaryItem = glossary
		.filter(({ term }) => term === filterTerm.toLowerCase())
		.map((data) => (
			<div key={data.term}>
				<h1>{data.term}</h1>
				<p>{data.definition}</p>
			</div>
		));
	return (
		<Container>
			<h1>Glossary</h1>
			<StyledSearchBar
				value={filterTerm}
				placeholder="Search for Terms"
				onChange={(e) => {
					setFilterTerm(e.target.value);
				}}></StyledSearchBar>

			{filterTerm !== '' ? glossaryItem : allGlossaryItems}
		</Container>
	);
};

export default GlossaryPage;
