import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import glossary from './../glossary';

const StyledSearchBar = styled.input`
	font-size: 1rem;
	padding: 1rem;
	border: #ccc solid 1px;
	border-radius: 5px;
	width: 100%;
`;

const GlossarySearch = (props) => {
	const [filterTerm, setFilterTerm] = useState('');
	// const filterGlossary = glossaryItem.filter((item) => {
	// 	return Object.keys(item).some((key) =>
	// 		item[key].toLowerCase().includes(lowercasedFilter)
	// 	);
	// });
	// .filter(({ term }) => term === filterTerm)

	const glossaryItem = glossary;
	filter((data) => {
		if (this.state.search == null) return data;
		else if (
			data.name.toLowerCase().includes(this.state.search.toLowerCase()) ||
			data.country.toLowerCase().includes(this.state.search.toLowerCase())
		) {
			return data;
		}
	}).map((data) => (
		<div key={data.term}>
			<h1>{data.term}</h1>
			<p>{data.definition}</p>
		</div>
	));

	console.log(glossaryItem);

	return (
		<div>
			<StyledSearchBar
				value={filterTerm}
				placeholder="Search for Terms"
				onChange={(e) => {
					setFilterTerm(e.target.value);
				}}></StyledSearchBar>
			{glossaryItem}
		</div>
	);
};

export default GlossarySearch;
