import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FlexBetween } from '../shared/global';
import glossary from './../glossary';
import { GrFormClose } from 'react-icons/fa';

const Modal = styled.div`
	background-color: #fff;
	width: 50%;
	position: absolute;
	top: 100;
	left: 0;
	right: 0;
	border: #ccc solid 1px;
	margin: 1rem auto;
	padding: 1rem;
`;

const StyledSearchBar = styled.input`
	font-size: 1rem;
	padding: 1rem;
	border: #ccc solid 1px;
	border-radius: 5px;
	width: 100%;
	position: relative;
`;

const GlossarySearch = (props) => {
	const [filterTerm, setFilterTerm] = useState('');
	// const filterGlossary = glossaryItem.filter((item) => {
	// 	return Object.keys(item).some((key) =>
	// 		item[key].toLowerCase().includes(lowercasedFilter)
	// 	);
	// });
	//

	const glossaryItem = glossary
		.filter(({ term }) => term === filterTerm.toLowerCase())
		.map((data) => (
			<div key={data.term}>
				<h1>{data.term}</h1>
				<p>{data.definition}</p>
			</div>
		));

	console.log(glossaryItem);

	return (
		<div>
			<StyledSearchBar
				value={filterTerm}
				placeholder="Search for Terms"
				onChange={(e) => {
					setFilterTerm(e.target.value);
				}}></StyledSearchBar>
			<Modal>
				<Title>
					<FlexBetween>
						<h1>Searching for {filterTerm}</h1>
						<CloseButton>
							<img></img>
						</CloseButton>
					</FlexBetween>
				</Title>

				{glossaryItem}
			</Modal>
		</div>
	);
};

export default GlossarySearch;
