import React, { useState } from 'react';
import styled from 'styled-components';
import { Container } from '../shared/global';

const StyledDiv = styled.div`
	display: flex;
	flex-direction: column;
	align-items: flex-start;

	& > * {
		margin: 0.2rem 0;
	}
`;

const SetBudget = () => {
	const [housing, setHousing] = useState(0);
	const [utilities, setUtilities] = useState(0);
	const [transportation, setTransportation] = useState(0);
	const [grocery, setGrocery] = useState(0);
	const [entertainment, setEntertainment] = useState(0);
	const [restaurants, setRestaurants] = useState(0);
	const [gifts, setGifts] = useState(0);
	const [pets, setPets] = useState(0);
	const [clothing, setClothing] = useState(0);
	const [health, setHealth] = useState(0);
	const [household, setHousehold] = useState(0);
	const [personal, setPersonal] = useState(0);

	return (
		<Container>
			<h1>Budget</h1>
			<StyledDiv>
				<input
					type="text"
					name="Housing"
					placeholder="Housing"
					onChange={(e) => {
						setHousing(e.target.value);
					}}></input>
				<input
					type="text"
					name="Utilities"
					placeholder="Utilities"
					onChange={(e) => {
						setUtilities(e.target.value);
					}}></input>
				<input
					type="text"
					name="Transportation"
					placeholder="Transportation"></input>
				<input type="text" name="Grocery" placeholder="Grocery"></input>
				<input
					type="text"
					name="Entertainment"
					placeholder="Entertainment"></input>
				<input type="text" name="Restaurants" placeholder="Restaurants"></input>
				<input type="text" name="Gifts" placeholder="Gifts"></input>
				<input type="text" name="Pets" placeholder="Pets"></input>
				<input type="text" name="Clothing" placeholder="Clothing"></input>
				<input type="text" name="Health" placeholder="Health"></input>
				<input type="text" name="Household" placeholder="Household"></input>
				<input type="text" name="Personal" placeholder="Personal"></input>
			</StyledDiv>

			<div>
				<p>housing: {housing}</p>
				<p>utilities: {utilities}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
				<p>housing: {housing}</p>
			</div>
		</Container>
	);
};

export default SetBudget;
