import React, { useState } from 'react';
import { Container, H1 } from '../shared/global';
import axios from 'axios';

const Game = (props) => {
	const [username, setUsername] = useState();
	const [role, setRole] = useState();

	const beginGame = (e) => {
		e.preventDefault();

		const user = {
			username: username,
			role: role,
		};

		axios
			.post('http://localhost:5000/users/add', user)
			.then((res) => console.log(res.data));

		console.log(username + 'is a' + role);
	};

	return (
		<Container>
			<label for="username">Username</label>
			<input placeholder="Write your username here"></input>
			<H1>What is your role?</H1>
			<button value="player" onClick={setRole('player')}>
				Player
			</button>
			<button>Gamemaster</button>
		</Container>
	);
};

export default Game;
